var NWEvent = (function(){
	'use strict';
	return new Class({
		
		callStack:			[], // an array of plugins to call
		elmt:			null, // the plugin elmt that fired the NWEvent
		originalEvent:	null, // the event that was used to create the NWEvent
		clickedElmt:	null, // the elmt that was actually clicked


		initialize: function(event, fireElmt){
			this.originalEvent = event;
			this.clickedElmt = event.target;
			this.elmt = fireElmt;
			this.initCallStack();
		},
		
		initCallStack: function() {
			var pluginsRegex = $NW.getPlugin('NWPlugins').pluginsRegex;
			var callStackPluginStrings = this.elmt.get('class').match(pluginsRegex); // get list of NovumWare classes to possibly call
			callStackPluginStrings.each(function(pluginString){
				var pluginName = pluginString.split(':')[0];
				var nwPlugin = $NW.getPlugin(pluginName);
				if (typeof nwPlugin.handleClick == 'function') this.callStack.push(pluginString);
			}.bind(this));
		},


		// ======================================================== PUBLIC METHODS ======================================
		addToCallStackBeginning: function(pluginString) {
			this.callStack.unshift(pluginString);
		},
		
		addToCallStackEnd: function(pluginString) {
			this.callStack.push(pluginString);
		},
		
		getNextPlugin: function() {
			if (this.callStack.length) { return this.callStack.shift(); }
			else { return null; }
		}

	});
})();


