var NWFormElmt = (function(){
	'use strict';

		return new Class({

		classPlugin: 'NWFormElmt',

		dataPreDCB: 'data-nwForm-preDCB',
		dataSuccessCB: 'data-nwForm-successCB',

		typeNormal: 'normal',
		typeAjax: 'ajax',
		typeJson: 'json',
		type: 'normal',
		
		elmt: null,
		validator: null,
		inputFileElmts: null,


		// ======================================================== SETUP METHODS ======================================
		initialize: function(elmt, options) {
			this.elmt = elmt;
			this.elmt.store(this.classPlugin, this);
			if (options.type) { this.type = options.type; }
			this.elmt.set('method', this.elmt.get('method') || 'post');
			this.validator = new NWValidator(this.elmt);
			// this.elmt.getElements('[type=submit]').addEvent('click', this.onSubmitClick.bind(this));
			// this.elmt.addEvent('submit', this.onSubmitClick.bind(this));
			this.initFileInputs();
		},
		
		initFileInputs: function() {
			var fileInputs = this.elmt.getElements('input[type=file]');
			if (!fileInputs.length) return;
			this.inputFileElmts = [];
			fileInputs.each(function(fileInput){
				this.inputFileElmts.push(new NWInputFileElmt(fileInput));
			}.bind(this));
		},


		// ======================================================== PUBLIC METHODS ======================================
		submitForm: function() {
			// TODO disable submit buttons during ajax
			if (this.validate() && $NW.callCB(this.elmt.get(this.dataPreDCB), this.elmt)) {
				if (this.type == this.typeAjax) { this.submitFormAjax(); }
				else if (this.type == this.typeJson) { this.submitFormJson(); }
				else { this.submitFormNormal(); }
			}
			// this.elmt.getElements("[type='submit']").set('disabled', true);
		},

		validate: function() {
			return this.validator.validate() || false;
		},


		// ======================================================== PRIVATE METHODS ======================================
		submitFormNormal: function() {
			this.elmt.submit();
		},

		submitFormAjax: function() {
			this.sendAjaxfully(this.typeAjax); // send the form
		},

		submitFormJson: function() {
			this.sendAjaxfully(this.typeJson); // send the form
		},

		sendAjaxfully: function(requestType) {
			var nwAjax = $NW.getPlugin('NWAjax');
			var o = {
				form: this.elmt,
				onRequest: function() {
					this.elmt.getElements('input[type="submit"], button[type="submit"]').set('disabled', 'disabled'); // disable submit buttons
					$NW.getPlugin('NWLoadingImage').show($(this.elmt.get(nwAjax.dataLoadingImageContainerId)));
				}.bind(this),
				onSuccess: function(data){ this.onFormSubmitSuccess(data); }.bind(this),
				onComplete: function() {
					this.elmt.getElements('input[type="submit"], button[type="submit"]').set('disabled', null); // enable submit buttons
					$NW.getPlugin('NWLoadingImage').hide($(this.elmt.get(nwAjax.dataLoadingImageContainerId)));
				}.bind(this)
			};
			if (requestType == this.typeJson) { nwAjax.jsonRequest(o, this.elmt); }
			else { nwAjax.htmlRequest(o, this.elmt); }
		},


		// ======================================================== EVENT HANDLERS ======================================
		// onSubmitClick: function(e) {
			// alert('su');
			// e.stop();
			// this.submitForm();
		// },
		
		onFormSubmitSuccess: function(data) {
			var dataSuccessCB = this.elmt.get(this.dataSuccessCB);
			if ($NW.callCB(dataSuccessCB, [data, this.elmt])) { $NW.callNextPlugin(); }
		}

	});
})();

