var NWInputFileElmt = (function(){
	'use strict';

	return new Class({

		Implements: [Options],

		classPlugin: 'NWInputFileElmt',
		
		dataMaxSize: 'data-nwInputFile-maxSize',
		dataAllowedTypes: 'data-nwInputFile-allowedTypes',
		dataShowImagePreview: 'data-nwInputFile-showImagePreview',
		dataImageCrop: 'data-nwInputFile-imageCrop',
		
		classThumbsList: 'nwInputFile-thumbsList',

		elmt: null,
		form: null,
		browseButtonelmt: null,
		thumbsListElmt: null,
		
		files: [],
		
		multiple: false,
		maxSize: null,
		allowedTypes: null,
		showImagePreview: true,
		imageCrop: false,


		// ======================================================== SETUP METHODS ======================================
		initialize: function(elmt) {
			// TODO get form submit working with ajax
			this.elmt = elmt;
			this.elmt.store(this.classPlugin, this);
			this.initForm();
			this.initData();
			this.initBrowseButton();
			this.initThumbsList();
			this.initElmtEvents();
		},
		
		initElmtEvents: function() {
			this.elmt.addEvent('change', this.onChange.bind(this));
		},
		
		initData: function() {
			this.multiple = (this.elmt.get('multiple')) ? true : false;
			this.maxSize = this.elmt.get(this.dataMaxSize);
			this.allowedTypes = this.elmt.get(this.dataAllowedTypes);
			var showImagePreview = this.elmt.get(this.dataShowImagePreview);
			if (showImagePreview) this.showImagePreview = !(showImagePreview == 'false' || showImagePreview == '0');
			var imageCrop = this.elmt.get(this.dataImageCrop);
			if (imageCrop) this.imageCrop = !(imageCrop == 'false' || imageCrop == '0');
		},
		
		initForm: function() {
			this.form = this.elmt.getParent('form').retrieve('NWFormElmt');
			this.form.elmt.set('enctype', 'multipart/form-data');
		},
		
		initBrowseButton: function() {
			if (this.form.type == this.form.typeNormal) return;
			this.elmt.hide();
			this.browseButtonElmt = new Element('button', {
				'text':	'Browse',
				'type':	'button'
			});
			this.browseButtonElmt.inject(this.elmt, 'before');
			this.browseButtonElmt.addEvent('click', this.onBrowseButtonClick.bind(this));
		},
		
		initThumbsList: function() {
			this.thumbsListElmt = new Element('ul', {
				'class': this.classThumbsList
			});
			this.thumbsListElmt.inject(this.elmt, 'after');
		},
		

		// ======================================================== PUBLIC METHODS ======================================


		// ======================================================== PRIVATE METHODS ======================================
		reset: function() {
			var oldElmt = this.elmt;
			this.elmt = oldElmt.clone();
			this.elmt.replaces(oldElmt);
			this.initElmtEvents();
		},

		// ======================================================== EVENT HANDLERS ======================================
		onBrowseButtonClick: function(e) {
			this.elmt.click();
		},
		
		onChange: function(e) {
			// TOOD could use better error reporting implementation
			// TODO clear input if validation failed
			var files = this.elmt.files;
			if (!this.multiple) this.thumbsListElmt.empty();
			Array.each(files, function(file){
				// console.log(file.name, file.type, file.size, file.lastModifiedDate.toLocaleDateString());
				if (this.allowedTypes && this.allowedTypes.indexOf(file.type) == -1) {
					$NW.getPlugin('NWFlashMessage').showErrorMessage('Uploads must be one of: '+this.allowedTypes);
					this.reset();
					return false;
				}
				if (this.maxSize && file.size > this.maxSize*1000) {
					$NW.getPlugin('NWFlashMessage').showErrorMessage('Uploads must be smaller than '+this.maxSize+' kB');
					this.reset();
					return false;
				}
				this.files.push(file);
				if (!this.showImagePreview) return;
				if (file.type.match(/image.*/)) { // if the file is an image
					var fileReader = new FileReader();
					fileReader.onloadend = function(e){ this.onReaderLoadEnd(e, fileReader, file); }.bind(this);
					// reader.onProgress
					// reader.onloadstart
					fileReader.readAsDataURL(file);
				}
			}.bind(this));
		},
		
		onReaderLoadEnd: function(e, fileReader, file) {
			var cropper;
			var newLiElmt = new Element('li');
			var newImageElmt = new Element('img.', {
				src: fileReader.result
			});
			newLiElmt.grab(newImageElmt);
			this.thumbsListElmt.grab(newLiElmt);
			if (!this.imageCrop) return;
			cropper = new uvumiCropper(newImageElmt, {onComplete: this.onCrop.bind(this)});
			newImageElmt.store('cropper', cropper);
		},
		
		onCrop: function(e) {
			// TODO implement image cropping
			console.log('crop');
		}
	});
})();

